fun <Model, Item : ListDisplayable> RadioFormBox<Model, Item>.DefaultFormRadioEntry(hint: Any?, modifier: Modifier = Modifier, textModifier: Modifier = Modifier, errorModifier: Modifier = Modifier, radioButton: (DisplayableOption<Item>, Int) -> Unit = { item, index ->
        Row(
            Modifier
                .fillMaxWidth()
                .selectable(
                    selected = index == value,
                    onClick = { setValue(index) },
                ).minimumInteractiveComponentSize(),
            verticalAlignment = Alignment.CenterVertically,
            horizontalArrangement = Arrangement.spacedBy(LocalFormStyle.current.fieldRowSpacing),
        ) {
            RadioButton(
                selected = (index == value),
                onClick = {
                    if (valueSnapshot == index) {
                        setValue(-1)
                    } else {
                        setValue(index)
                    }
                },
                enabled = enabled,
            )
            Text(
                text = item.label,
            )
        }
    })

