fun FormBox<*, TextFieldValue>.DefaultDateEntry(modifier: Modifier = Modifier.fillMaxWidth(), hint: Any? = null, placeholder: Any? = null, isLast: Boolean = false, leadingIcon: () -> Unit? = null, datePickerState: DatePickerState = rememberDatePickerState(), allowTyping: Boolean = true, entry: FormBox<*, TextFieldValue>.(DatePickerController) -> Unit = {
        val enabled = enabled
        DefaultTextEntry(
            hint = hint,
            modifier =
                modifier.onFocusChanged { focusState ->
                    if (focusState.isFocused && !allowTyping && enabled) {
                        it.setPickerVisible(true)
                    }
                },
            icon =
                if (enabled) {
                    FormScope.IconParams(
                        Icons.Filled.DateRange,
                    ) {
                        it.setPickerVisible(true)
                    }
                } else {
                    null
                },
            placeholder = placeholder,
            isLast = isLast,
            keyboardOptions = KeyboardOptions(keyboardType = KeyboardType.Number),
            leadingIcon = leadingIcon,
            readOnly = !(allowTyping && enabled),
        )
    }, dialog: FormBox<*, TextFieldValue>.(DatePickerController) -> Unit = {
        DefaultDatePickerDialog(
            it.datePickerState,
            ::setValue,
        ) {
            it.setPickerVisible(false)
        }
    })

